Insert into ITMT_I (ID,ID_IDIOMA,TITULO,ENTRADILLA,ALTIMAGEN,ENTRADILLA_LISTA_DESCARGA) values ('1676','0','Cuadernos de Educación no Sexista','<p style="margin-left: 40px;"><span style="color: rgb(0, 51, 102);">Esta colecci&oacute;n va dirigida a todas aquellas personas que quieran educar en el desarrollo equilibrado de chicas y chicos, en unas relaciones sanas, libres de violencia; desde un acompa&ntilde;amiento en sus procesos de crecimiento basado en la confianza, el reconocimiento de sus capacidades y el cuidado de la relaci&oacute;n. Se trata de materiales coeducativos con reflexiones y orientaciones significativas en torno a la prevenci&oacute;n de la violencia sexista, el cuidado de las relaciones, la educaci&oacute;n afectivo-sexual, el reconocimiento de las aportaciones femeninas a la historia, las ciencias, las artes, el deporte, las tecnolog&iacute;as; las leyes relacionadas con el logro de la igualdad en el &aacute;mbito educativo, la experiencia de mujeres Sindicadas.</span></p>',null,null);
