Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2810','0','ley 4/2005','Situaci&oacute;n de desventaja social derivada de la concurrencia en una misma persona o grupo social de m&aacute;s de un factor que puede dar lugar a la discriminaci&oacute;n. En el contexto de la igualdad de mujeres y hombres, se refiere a mujeres o grupos de mujeres que, adem&aacute;s de la discriminaci&oacute;n por raz&oacute;n de su sexo, sufren otras discriminaciones por motivo de su raza, color, origen &eacute;tnico, lengua, religi&oacute;n, opiniones pol&iacute;ticas o de otro tipo, pertenencia a una minor&iacute;a nacional, patrimonio, nacimiento, discapacidad, edad, orientaci&oacute;n sexual o cualquier otra condici&oacute;n o circunstancia personal o social.<br /><strong>Basado en <em>Principios Generales del Art&iacute;culo 3 de Ley 4/2005, de 18 de febrero, para la Igualdad de Mujeres y Hombres. </em>Gobierno Vasco.</strong>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2811','0','Ed en relacion','<p>Educar es un acto de relaci&oacute;n, de encuentro entre dos personas con grandes diferencias de edad y de saber, donde quien ense&ntilde;a asume la responsabilidad de ayudar a crecer a quienes, impl&iacute;citamente, le reconocen autoridad para hacerlo y aceptan seguir sus directrices y consejos.<br /><strong>MONTOYA, M&ordf; Milagros, &ldquo;Las tutor&iacute;as &iquest;un espacio privilegiado para las relaciones?&rdquo; En V.V.A.A. <em>Educar en Relaci&oacute;n,</em> Serie de Cuadernos de Educaci&oacute;n No Sexista N&ordm; 6, Madrid, Instituto de la Mujer, 2002. P. 45.</strong></p><p>&nbsp;</p>',null);
