Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2809','0','Tomar en serio..., La ed. sexual primera infancia','<p><br />La diferencia sexual hace referencia al sentido y el significado que cada cual le da al hecho de nacer siendo de un sexo o de otro. Vivir este hecho con interpretaciones impuestas o bien como si el propio cuerpo no tuviera ninguna significaci&oacute;n hace casi imposible vivir a gusto en la propia piel. Esto es as&iacute; porque dar un sentido libre y singular al sexo que se tiene es una necesidad existencial: es poder realizar deseos propios y originales sin caricaturizar ni renunciar a aquello que se es (un hombre y una mujer).<br /><strong>HERN&Aacute;NDEZ MORALES, Graciela y JARAMILLO GUIJARRO, Concepci&oacute;n. <em>La educaci&oacute;n sexual de la primera infancia Gu&iacute;a para madres, padres y profesorado de educaci&oacute;n infantil,</em> Madrid, CIDE e Instituto de la Mujer, 2002. &nbsp;P. 18.</strong></p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2895','0','CEDAW','Denota toda distinci&oacute;n, exclusi&oacute;n o restricci&oacute;n basada en el sexo que tenga por objeto o por resultado menoscabar o anular el reconocimiento, goce o ejercicio por la mujer,&nbsp; independientemente de su estado civil, sobre la base de la igualdad del hombre y la mujer, de los derechos humanos y las libertades fundamentales en las esferas pol&iacute;tica, econ&oacute;mica, social, cultural y civil o en cualquier otra esfera.&nbsp;<br /><strong><em>Art&iacute;culo 1 de la CEDAW,</em> <em>Convenci&oacute;n de Eliminaci&oacute;n de Todas las Formas de Discriminaci&oacute;n sobre la Mujer, </em>de las Naciones Unidas.</strong>',null);
