Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2879','0','la activ. cientifica en la cocina','Los conocimientos de las mujeres relacionados con el hogar no son un conjunto de pr&aacute;cticas caseras, de conocimientos aprendidos por transmisi&oacute;n oral, sin una instrucci&oacute;n espec&iacute;fica, transmitidos de madres a hijas, sin ninguna relaci&oacute;n entre ellos. Las mujeres, de acuerdo con el funcionamiento cognitivo de las personas, no tienen ideas aisladas de las cosas, sino que disponen de un conjunto integrado de conocimientos con una consistencia interna que, como grupo, han elaborado y adaptado a las necesidades de cada momento hist&oacute;rico. Es decir, un conjunto de explicaciones, un conjunto de ideas sobre las tareas que hay que realizar para el buen funcionamiento de la familia y el bienestar de sus integrantes.&nbsp;<br /><strong>SOLSONA I PAIR&Oacute;, Nuria, <em>La actividad cient&iacute;fica en la cocina,</em> Educaci&oacute;n primaria, Serie Cuaderno de Educaci&oacute;n No Sexista N&ordm; 12, Madrid, Instituto de la Mujer, 2002. P. 8.<br /></strong><br /><br />El objetivo de estas actividades (&hellip;) es revalorizar la importancia de algunos <strong>saberes femeninos</strong>, en concreto del conocimiento dom&eacute;stico, en nuestro entorno y destacar su car&aacute;cter de tarea indispensable para la vida de las personas y el buen funcionamiento de las familias y la sociedad. El conocimiento dom&eacute;stico incluye una gran cantidad de conocimientos matem&aacute;ticos, cient&iacute;ficos y sociales.<br /><strong>SOLSONA I PAIR&Oacute;, Nuria, <em>La actividad cient&iacute;fica en la cocina,</em> Educaci&oacute;n primaria, Serie Cuaderno de Educaci&oacute;n No Sexista N&ordm; 12, Madrid, Instituto de la Mujer, 2002. P. 7.</strong>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4208','0','María Vaíllo','<p><span style="color: rgb(128, 0, 128);"><strong>MUJERES CIENT&Iacute;FICAS EN DIFERENTES ETAPAS DE LA HISTORIA. Cat&aacute;logo para completar el actual curriculum escolar</strong><br /></span><br />La Ciencia griega: <br />&bull; La escuela pitag&oacute;rica. <br />&bull; Las mujeres en la Grecia cl&aacute;sica: Aspasia, Diotima. <br />&bull; El periodo helen&iacute;stico: la ciencia en Alejandr&iacute;a. <br />&bull; La ciencia grecorromana: Cleopatra, Aspasia. <br />&bull; Mar&iacute;a la Jud&iacute;a y el florecimiento de la Alquimia. <br />&bull; Hypatia de Alejandr&iacute;a. <br /><br />La Edad Media: <br />&bull; Tr&oacute;tula de Salerno.<br />&bull; Hildegarda de Bingen.<br /><br />La revoluci&oacute;n cient&iacute;fica: <br />&bull; Las mujeres en el Renacimiento.<br />&bull;  Las mujeres cient&iacute;ficas en los talleres artesanales: Mar&iacute;a Cunita,  Elisabeth Korpman, Mar&iacute;a Winckelmann Kirch, Mar&iacute;a Sibyla Merian, Mar&iacute;a  Van Schurman.<br />&bull; Caroline Heschel y la Astronom&iacute;a sideral.<br />&bull; Las comadronas. <br />&bull; Las mujeres en la medicina moderna: Louise Bourgeois, Olivia Sabuco de Nantes Barrera, Dorotea Erxleben. <br />&bull; Las academias cient&iacute;ficas: Margaret Cavendish, Emile du Ch&acirc;telet, Lise Meitnier, Irene Joliot-Curie.<br />&bull; Los salones cient&iacute;ficos: Mm. Goeffein, Mm. Helv&eacute;tius, Mm. Rochefoucauld y Mm. Lavoisier.<br />&bull; Margaret Cavendish y la Real Sociedad de Londres. <br />&bull; Lady Mary Montagui: precursora de la vacuna.<br />&bull; La f&iacute;sica newtoniana entra en el continente: Emile du Ch&acirc;telet. <br />&bull; Marie Lavoisier y el nacimiento de la qu&iacute;mica moderna.<br />&bull;  Las mujeres cient&iacute;ficas en las universidades italianas: Elena Cornaro  Piscopia, Laura Mar&iacute;a Catarina Bassi, Mar&iacute;a Gaetana Agnesi, Ana Morandi  Mazolini, Luc&iacute;a Galvani. El siglo XIX: el fin de la ciencia amateur <br />&bull; Sophie Germain: &ldquo;premio extraordinario&rdquo; de la Academina. <br />&bull; Los or&iacute;genes del ordenador: Ada Lovelace. <br />&bull; Sonia Kovalevskaia: la primera mujer de la Academia de Ciencias de Rusia.<br />&bull; El fin de la ciencia amateur: Mary Somerville.<br /><br />&nbsp;El siglo XX: la especializaci&oacute;n cient&iacute;fica:<br />&bull;  Marie Curie, Henrietta Swan Leavitt, Lise Meitner, Ida Eva Tacke  Noddack, Gerty Theresa Cori, Irene Joliot-Curie, B&aacute;rbara McClintock,  Kathleen Lonsdale, Mar&iacute;a Goeppert Mayer, Dorothy Crowfood-Hodgkin, Rita  Levi-Montalcini, Rosalind Franklin, Mileva Maric. <br /><br /><br /><strong><strong>VA&Iacute;LLO RODR&Iacute;GUEZ, </strong>Mar&iacute;a.&nbsp; <em>Recomendaciones para introducir la igualdad e innovar en los libros de texto. </em>Instituto de la Mujer, Madrid, 2013, p.75</strong><br />&nbsp;</p>',null);
