Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','89',null,'<strong><font color="#ff6600">In questa sezione conoscerete la legislazione vigente e la realt&agrave; statistica in materia di educazione e di&nbsp;parit&agrave; delle opportunit&agrave; tramite l''Assessorato per l''Educazione e l''Organismo&nbsp;per l''Uguaglianza di ogni Comunit&agrave; Autonoma.<br /><br />Inoltre, si possono trovare i progetti e le iniziative educative che, a livello&nbsp;regionale,&nbsp;vengono man mano sviluppati intorno alla coeducazione.</font></strong>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3121','11','O que é?','Nesta sec&ccedil;&atilde;o podereis conhecer e aprofundar o significado de termos e express&otilde;es relacionadas com a educa&ccedil;&atilde;o, a igualdade de oportunidades e o movimento das mulheres.<br /><br />Longe de pretender dar a cada termo um significado un&iacute;voco, s&atilde;o propostas diferentes aproxima&ccedil;&otilde;es que v&aacute;rias autoras e autores realizaram, com o objectivo de oferecer poss&iacute;veis&nbsp;significados.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3121','1','Description','<p><span style="color: #ff00ff">In this section, you can discover the meaning of the terminology and expressions related to education, equal opportunity and the women''s movement.</span></p><p><span style="color: #ff00ff">Far from attempting to give each term a fixed meaning, we provide the different definitions proposed by various women and men authors in order to delve into the key concepts.</span></p>',null);
