Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','159',null,'<ul>    <li>Hezkuntza administrazioei, berdintasuneko erakundeei eta interesa duten erakunde zein pertsonei, emakumezkoen eta gizonezkoen hezkuntza esperientziak eta ezagutzak partekatzeko bidea ematea.</li>    <li>Hezkuntza komunitateari bi sexuak kontuan hartzen dituen, emakumezkoena onartu eta balioesten duena, gizonezkoen azpitik jarri gabe, hezitzeko modua ahalbidetzen duten baliabideak eskura jartzea.</li>    <li>Parte hartzeko, erraz sartu eta erabiltzeko, gunea sortzea.</li>    <li>Espainiar testuinguruan aplika daitekeen beste hainbat herrialdeko esperientzia berritzaileak eta originalak partekatzea.</li></ul>',null);
