Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','1','Text','<p><strong><span style="color: #ff6600">This section includes current legislation and statistics concerning educational issues and equal opportunity provided by the Departments of Education and the Agencies for Equality of each Autonomous Region.</span></strong></p><p><strong><span style="color: #ff6600">Furthermore, this section also includes educational projects and initiatives developed on a regional level concerning co-education.</span></strong></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3133','1','Text','<p><span style="color: #99cc00">This section includes current legislation concerning co-education and equal opportunity within four scopes: international, European, national and regional. Documents and various relevant texts are organised according to these scopes and may be downloaded in pdf format.</span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3133','89',null,'<span lang="IT"><font face="Times New Roman" size="3"><strong><font color="#99cc00"><p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt"><span lang="IT"><font face="Times New Roman" size="3"><strong><font color="#99cc00">In questa sezione conoscerete la legislazione vigente</font><font color="#99cc00"> </font><font color="#99cc00">in materia di coeducazione e di parit&agrave; delle opportunit&agrave;&nbsp;nei seguenti&nbsp;quattro ambiti: internazionale, comunitario, statale e regionale. A tal fine, figurano documenti e diversi testi di un certo interesse che si possono scaricare in formato PDF.</font></strong></font></span></p></font></strong></font></span>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3137','11','Texto','Nesta sec&ccedil;&atilde;o encontrareis materiais educativos elaborados pelo CIDE e o Instituto da Mulher, algumas vezes em conjunto. S&atilde;o materiais que abordam a realidade educativa que as meninas e os meninos vivem e que prop&otilde;em novas formas de educar que tenham em aten&ccedil;&atilde;o esta diferen&ccedil;a sexual.',null);
