Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3128','1','Text','<p><strong><span style="color: #339966">This section includes information concerning education and women in the world, along with various co-educational initiatives carried out by women around the world. Furthermore, it also includes a section on the lives and work of important women within the educational and cultural spheres, in order to construct a feminine genealogy that promotes and acknowledges women''s work.</span></strong></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3129','1','Text','<p><strong><span style="color: #000080">This section includes the names of research groups, associations and organisations related to women and education.</span></strong></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','11','Texto','Nesta sec&ccedil;&atilde;o conhecereis a legisla&ccedil;&atilde;o vigente e a realidade estat&iacute;stica em mat&eacute;ria educativa e de igualdade de oportunidades atrav&eacute;s da Secretaria de Educa&ccedil;&atilde;o e o Organismo de Igualdade de cada Comunidade Aut&oacute;noma.<br /><br />Al&eacute;m disso, incluem-se os projectos e as iniciativas educativas que, a n&iacute;vel auton&oacute;mico, se desenvolvem &agrave; volta da coeduca&ccedil;&atilde;o.',null);
