Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3128','11','Texto','Esta sec&ccedil;&atilde;o compila informa&ccedil;&atilde;o sobre a educa&ccedil;&atilde;o e as mulheres no mundo, juntamente com as v&aacute;rias iniciativas coeducativas empreendidas pelas mulheres no mundo. Al&eacute;m disso, inclui uma sec&ccedil;&atilde;o na qual se cede um espa&ccedil;o &agrave; vida e obra de mulheres significativas dentro do &acirc;mbito educativo e cultural, com o objectivo de construir uma genealogia feminina que ofere&ccedil;a referentes e torne mais vis&iacute;vel o trabalho das mulheres.',null);
