Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3137','48','Text','<p class="MsoNormal" style="MARGIN: 0cm 0cm 0pt; LINE-HEIGHT: normal; TEXT-ALIGN: justify"><strong><span lang="CA" style="FONT-SIZE: 12pt; COLOR: #cc0033; FONT-FAMILY: &quot;Times New Roman&quot;; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: CA; mso-fareast-language: ES">En aquesta secci&oacute; trobareu&nbsp; materials educatius elaborats pel CIDE i l&rsquo;Institut de la Dona, a vegades conjuntament. S&oacute;n materials que centren la seua mirada en la realitat educativa que viuen xiquetes i xiquets i proposen maneres d&rsquo;educar que tinguen en compte aquesta difer&egrave;ncia sexual.&nbsp;</span></strong><span lang="CA" style="FONT-SIZE: 12pt; FONT-FAMILY: &quot;Times New Roman&quot;; mso-fareast-font-family: ''Times New Roman''; mso-ansi-language: CA; mso-fareast-language: ES"><p>&nbsp;</p></span></p>',null);
