Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','89',null,'<ul>    <li>Fornire alle Amministrazioni Educative, agli Organismi di Parit&agrave;, alle istituzioni e persone interessate uno strumento per lo scambio di conoscenze ed&nbsp;esperienze educative al femminile e al maschile. </li></ul><p>&nbsp;</p><ul>    <li>Dare la possibilit&agrave; a tutta la comunit&agrave; educativa di accedere a delle&nbsp;risorse che mostrano un modo di educare che tiene in conto il contributo educativo di entrambi i sessi, e riconosce e valorizza quello&nbsp;femminile senza subordinarlo al maschile. </li></ul><p>&nbsp;</p><ul>    <li>Creare un portale accessibile, facile da usare e aperto alla partecipazione. </li></ul><p>&nbsp;</p><ul>    <li>Condividere con altri paesi esperienze innovatrici, originali ed applicabili al contesto spagnolo. </li></ul><p><br /><br /><br /></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','112',null,'<li>Proporcionarlles &aacute;s Administraci&oacute;ns Educativas e aos Organismos de Igualdade, as&iacute; como&nbsp;&aacute;s entidades e persoas interesadas, un medio para o intercambio de co&ntilde;ecementos e de experiencias educativas en feminino e en masculino.<br /></li><li>Posibilitarlle a toda a comunidade educativa o acceso a recursos que mostran un xeito de educar que ten en conta ambos os sexos, que reco&ntilde;ece e valora o feminino sen subordinalo ao masculino.<br /></li><li>Crear un portal accesible, de f&aacute;cil&nbsp;uso e manexo e aberto &aacute; participaci&oacute;n.<br /></li><li>Compartir experiencias innovadoras e orixinais aplicables ao contexto espa&ntilde;ol con outros pa&iacute;ses.</li>',null);
