Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3128','147','Text','<p>Aquesta secci&oacute; recull informaci&oacute; sobre l''educaci&oacute; i les dones al m&oacute;n, juntament amb les variades iniciatives coeducativas empreses per les dones al m&oacute;n. A m&eacute;s, inclou un apartat en el qual se cedeix un espai a la vida i obra de dones significatives dins de l''&agrave;mbit educatiu i cultural, amb el prop&ograve;sit de construir una genealogia femenina que ofereixi referents i visualitzi la tasca de les dones.</p><p>&nbsp;</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3129','147','Text','<p>En aquesta secci&oacute; podreu trobar grups d''investigaci&oacute;, associacions i organitzacions que s''interessen per les dones i l''educaci&oacute;. <br />&nbsp;</p>',null);
