Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3600','11',null,'<p><span lang="PT" new="" times="" style="line-height: 115%; font-family: ">O primeiro Encontro Intercambia teve lugar em <b>2005</b>. Desde essa data, foram realizadas importantes iniciativas, tais como o compromisso s&oacute;lido das diferentes Comunidades Aut&oacute;nomas para a cria&ccedil;&atilde;o de uma rede de colabora&ccedil;&atilde;o interauton&oacute;mica, atrav&eacute;s da qual se pudessem coordenar esfor&ccedil;os, partilhar informa&ccedil;&atilde;o e difundir boas pr&aacute;ticas &agrave; volta da coeduca&ccedil;&atilde;o (a denominada <b>Rede Intercambia</b>), assim como o arranque e funcionamento do site web &quot;Intercambia: educar em feminino e em masculino&quot;, fruto de um trabalho cont&iacute;nuo de comunica&ccedil;&atilde;o e difus&atilde;o conjunta.</span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3601','11',null,'<p><span lang="PT" new="" times="" style="line-height: 115%; font-family: ">Alguns dos temas abordados pelos diferentes Encontros Intercambia foram a <b>linguagem e a mudan&ccedil;a cultural, </b>a <b>educa&ccedil;&atilde;o do cuidado com o corpo, </b>o <b>amor e a sexualidade</b>, a f<b>orma&ccedil;&atilde;o dos professores</b> na coeduca&ccedil;&atilde;o ou a <b>orienta&ccedil;&atilde;o acad&eacute;mica e profissional</b> para a igualdade de oportunidades entre ambos os sexos. Fruto de cada um deles, foram editados os </span><span new="" style="line-height: 115%; font-family: "><a href="http://www.educacion.es/cide/jsp/plantilla.jsp?id=pubintercambia"><span lang="PT" new="" times="" style="line-height: 115%; font-family: ">Cat&aacute;logos Intercambia</span></a></span><span lang="PT" new="" times="" style="line-height: 115%; font-family: ">, nos quais se recolhem as diferentes publica&ccedil;&otilde;es e projectos empreendidos tanto pelo IFIIE, o Instituto da Mulher e as diversas Comunidades e Cidades Aut&oacute;nomas no que diz respeito aos temas tratados.</span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3599','17',null,'<p>&Icirc;nt&acirc;lnirile Intercambia au ca scop <strong>schimbul de experien&#355;e, proiecte &#351;i materiale didactice inovatoare cu privire la egalitatea de oportunit&#259;&#355;i &icirc;n educa&#355;ie elaborate de c&#259;tre Organismele de promovare a egalit&#259;&#355;ii &#351;i Departamentele de Educa&#355;ie ale diverselor Comunit&#259;&#355;i autonome</strong>. Pentru aceasta, IFIIE &#351;i Institutul pentru drepturile femeii organizeaz&#259; anual o &icirc;nt&acirc;lnire av&acirc;nd o tem&#259; de interes &icirc;n jurul c&#259;reia se poart&#259; dezbateri, se traseaz&#259; ac&#355;iuni specifice &#351;i se comunic&#259; diverse activit&#259;&#355;i realizate.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3600','17',null,'<p>Prima &Icirc;nt&acirc;lnire Intercambia a avut loc &icirc;n <strong>2005</strong>. De atunci au fost materializate ini&#355;iative importante, precum angajamentul solid al diverselor autonomii &icirc;n crearea unei re&#355;ele de colaborare inter-autonomic&#259; prin care se urm&#259;re&#351;te coordonarea eforturilor, realizarea schimbului de informa&#355;ii &#351;i transmiterea de practici utile privind coeduca&#355;ia (a&#351;a-numita <strong>Re&#355;ea Intercambia</strong>), precum &#351;i activarea portalului &quot;Intercambia: educa&#355;ia la feminin &#351;i masculin&quot;, rezultat al unui efort continuu de comunicare &#351;i diseminare. </p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3601','17',null,'<p>Printre temele abordate de c&#259;tre diversele &Icirc;nt&acirc;lniri Intercambia men&#355;ion&#259;m <strong>limbajul &#351;i schimbul cultural, educa&#355;ia privind &icirc;ngrijirea corpului, dragostea &#351;i sexualitatea, formarea profesoratului &icirc;n coeduca&#355;ie sau orientarea academic&#259; &#351;i profesional&#259;</strong> pentru a da &#351;anse egale ambelor sexe. &Icirc;n urma acestor &Icirc;nt&acirc;lniri au fost constituite <a href="http://www.educacion.es/cide/jsp/plantilla.jsp?id=pubintercambia">Cataloagele Intercambia</a>, &icirc;n care se includ diversele publica&#355;ii &#351;i proiecte realizate de IFIIE, de Institutul pentru promovarea femeii &#351;i diversele Comunit&#259;&#355;i &#351;i Ora&#351;e Autonome cu privire la temele tratate.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','183',null,'<ul type="disc">    <li class="MsoNormal" style="MARGIN: 0cm 0cm 10pt; LINE-HEIGHT: normal; TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; mso-list: l0 level1 lfo1; tab-stops: list 36.0pt"><span lang="FR" style="FONT-SIZE: 12pt; FONT-FAMILY: &quot;Times New Roman&quot;; mso-fareast-font-family: ''Times New Roman''; mso-fareast-language: ES">Offrir aux administrations acad&eacute;miques, aux organismes d&rsquo;&eacute;galit&eacute; et aux entit&eacute;s et personnes int&eacute;ress&eacute;es, un moyen permettant l&rsquo;&eacute;change de connaissances et d&rsquo;exp&eacute;riences &eacute;ducatives au f&eacute;minin et au masculin.    <p>&nbsp;</p>    </span></li></ul><ul type="disc">    <li class="MsoNormal" style="MARGIN: 0cm 0cm 10pt; LINE-HEIGHT: normal; TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; mso-list: l3 level1 lfo2; tab-stops: list 36.0pt"><span lang="FR" style="FONT-SIZE: 12pt; FONT-FAMILY: &quot;Times New Roman&quot;; mso-fareast-font-family: ''Times New Roman''; mso-fareast-language: ES">Permettre &agrave; toute la communaut&eacute; &eacute;ducative l&rsquo;acc&egrave;s &agrave; des ressources qui montrent une mani&egrave;re d&rsquo;&eacute;duquer tenant compte des deux sexes, reconnaissant et &eacute;valuant le f&eacute;minin sans le subordonner au masculin.    <p>&nbsp;</p>    </span></li></ul><ul type="disc">    <li class="MsoNormal" style="MARGIN: 0cm 0cm 10pt; LINE-HEIGHT: normal; TEXT-ALIGN: justify; mso-margin-top-alt: auto; mso-margin-bottom-alt: auto; mso-list: l1 level1 lfo3; tab-stops: list 36.0pt"><span lang="FR" style="FONT-SIZE: 12pt; FONT-FAMILY: &quot;Times New Roman&quot;; mso-fareast-font-family: ''Times New Roman''; mso-fareast-language: ES">Cr&eacute;er un portail accessible, &agrave; l&rsquo;utilisation et au maniement faciles et ouvert &agrave; la participation.    <p>&nbsp;</p>    