Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3137','182',null,'<p>&nbsp;</p><p class="MsoNormal" style="margin-bottom: 0pt; line-height: normal"><b><span lang="DE" new="" times="" style="font-size: 12pt; font-family: ">In diesem Abschnitt finden Sie Lehrmaterialien, die von CIDE und dem Instituto de <st1:personname w:st="on" productid="la Mujer">la Mujer</st1:personname> (Institut f&uuml;r die Frau) &ndash; teilweise gemeinsam - erarbeitet wurden. Diese Materialien ber&uuml;cksichtigen die Bildungsrealit&auml;t von Jungen und M&auml;dchen und sollen zu einer Erziehung beitragen, die beiden Geschlechtern gerecht wird.&nbsp;</span></b><span lang="DE" new="" times="" style="font-size: 12pt; font-family: "><o:p></o:p></span></p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','182',null,'<p>&bull; Den Bildungsbeh&ouml;rden und Gleichbehandlungsorganismen sowie allen interessierten Institutionen und Personen ein Medium zum Austausch von Kenntnissen und Erfahrungen rund um die Erziehung auf m&auml;nnlich und weiblich zur Verf&uuml;gung zu stellen.</p><p><br />&bull; Allen im Erziehungsbereich t&auml;tigen Personen den Zugang zu Materialien zu erm&ouml;glichen, mit denen eine zwischen den beiden Geschlechtern differenzierte Erziehung m&ouml;glich ist, die das Weibliche anerkennt, ohne es dem M&auml;nnlichen unterzuordnen.</p><p><br />&bull; Ein einfach zu handhabendes, benutzerfreundliches Portal zur schaffen, das zur Teilnehme einl&auml;dt.</p><p><br />&bull; Innovative und neue Erfahrungen aus anderen L&auml;ndern kennen zu lernen, die auf Spanien anwendbar sind.<br />&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3121','182','Was ist das','<p>In diesem Abschnitt finden Sie Definitionen und Informationen zu verschiedenen Begriffen rund um Erziehung, Chancengleichheit und Frauenbewegung.</p><p>Hier wird nicht versucht, allgemeing&uuml;ltige Definitionen festzulegen, sondern die Zug&auml;nge verschiedener Autoren und Autorinnen werden miteinander kontrastiert, um die Vielschichtigkeit der Begriffe deutlich zu machen.</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','182',null,'<p>In diesem Abschnitt finden Sie Informationen zur Gesetzgebung und der statistischen Realit&auml;t zu Erziehung und Chancengleichheit. Diese Informationen werden von den Erziehungsministerien und den Gleichbehandlungsinstitutionen der einzelnen Autonomen Regionen zur Verf&uuml;gung gestellt.</p><p>Daneben finden Sie Informationen &uuml;ber Erziehungsinitiativen und &ndash;projekte der einzelnen Autonomen Regionen zum Thema Koedukation.</p><p>&nbsp;</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3133','182',null,'<p>In diesem Abschnitt finden Sie die derzeit g&uuml;ltigen f&uuml;r die Koedukation und Chancengleichheit relevanten Gesetze in vier verschiedenen Bereichen: International, EU, Spanien und Autonome Regionen. Die Sammlung umfasst verschiedene Dokumente und relevante Texte, die auch als pdf-Download zur Verf&uuml;gung stehen.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3387','182',null,'<p>Dieser Abschnitt bietet eine Auswahl an statistischen Quellen zum Thema Koedukation und Chancengleichheit in vier Bereichen: International, EU, Spanien und Autonome Regionen. Au&szlig;erdem finden Sie relevante Dokumente als pdf-Download.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3128','182',null,'<p>In diesem Abschnitt finden Sie Informationen &uuml;ber Frauen und Erziehung auf der gesamten Welt, zusammen mit verschiedenen Initiativen zur Koedukation, die von Frauen auf der ganzen Welt gestartet wurden. In einem Unterabschnitt finden Sie Informationen &uuml;ber das Leben und Werk von Frauen, die viel im koedukativen und kulturellen Bereich geleistet haben, um eine weibliche Genealogie zu schaffen, die als Beispiel dient und die Arbeit der Frauen auf der Welt sichtbar macht.</p>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3129','182',null,'<p>In diesem Abschnitt finden Sie Forschungsgruppen, Vereinigungen und Organisationen, die sich mit Frauen und Erziehung besch&auml;ftigen.</p>',null);
