Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3573','0','programas','<ul>    <li><a href="http://www.educarex.es/web/guest/proeduayu/educacion-para-la-igualdad-y-la-no-violencia"><strong>Educar en relaci&oacute;n para prevenir la violencia:</strong></a> curso de formaci&oacute;n destinado al personal de la Junta de  Extremadura dentro del Plan de Formaci&oacute;n de la Escuela de Administraci&oacute;n  P&uacute;blica (E.A.P.). Las personas asistentes han formado parte de los equipos de educaci&oacute;n social de Centros de  Menores, Orientaci&oacute;n y personal del Instituto de la Mujer de  Extremadura.</li></ul><ul>    <li><a href="http://www.educarex.es/web/guest/proeduayu/educacion-para-la-igualdad-y-la-no-violencia"><strong>Programas para la Igualdad y la No violencia</strong></a>: incluye programas como <em>Mujer, Arte y G&eacute;nero</em>, que comprende una conferencia, exposici&oacute;n y proyecci&oacute;n de videos de 6 artistas con perspectiva de g&eacute;nero. Performance con la participaci&oacute;n del alumnado de secundaria.</li></ul><p>&nbsp;</p>',null);
