Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3884','0','Hellen Keller y Anne Sullivan','<p style="margin-left: 40px;"><span style="color: rgb(128, 0, 128);"><strong>Hellen Keller (1880-1968), primera mujer sordociega que fue a la universidad, </strong></span><span style="color: rgb(128, 0, 128);"><strong>activista, autora y oradora</strong></span>:</p><ul>    <li><a href="http://es.wikipedia.org/wiki/Helen_Keller">Vida, obra y enlaces</a> (Wikipedia)</li>    <li><a href="http://www.apocatastasis.com/helen-keller.php#axzz20mAvbRhp">Biograf&iacute;a, aportaciones y reportaje sobre su vida</a></li>    <li><a href="http://issuu.com/atalantaweb/docs/66_-_keller_issuu?mode=embed&amp;layout=http%3A%2F%2Fskin.issuu.com%2Fv%2Fcolor%2Flayout.xml&amp;backgroundColor=000000&amp;showFlipBtn=true&amp;autoFlip=true&amp;autoFlipTime=6000">Libro &quot;El mundo en el que vivo&quot; (1908)</a>. Imaginatio Vera. Vila&uuml;r: Ediciones Atalanta.</li>    <li><a href="http://www.swedenborg.es/helen_keller/helen_keller.htm">Libro &quot;Luz en mi oscuridad&quot;</a>.</li></ul><p>&nbsp;</p><p style="margin-left: 40px;"><span style="color: rgb(128, 0, 128);"><strong>Anne Sullivan (1866-1936), maestra de personas sordociegas y escritora</strong></span></p><ul>    <li><a href="http://es.wikipedia.org/wiki/Anne_Sullivan">Vida, referencias, enlaces</a> (Wikipedia).</li>    <li><a href="http://www.afb.org/asm/">Biograf&iacute;a (en ingl&eacute;s, Fundaci&oacute;n Americana para personas ciegas)</a>.</li>    <li><a href="http://mujeresquehacenlahistoria.blogspot.com.es/2009/04/siglo-xix-annie-sullivan.html">Biograf&iacute;a</a>, en Mujeres que hacen la Historia</li>    <li><a href="http://www.imdb.com/title/tt0056241/awards">Pel&iacute;cula &quot;El milagro de Anna Sullivan (The miracle worker, 1962)</a>&quot;</li></ul><p>&nbsp;</p>',null);
