Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('820','0','p03','Algunas impresoras, sin embargo, tienen que mandar las fuentes True Type como gr&aacute;ficos. Para hacer esto, dentro de Inicio vamos&nbsp;Configuraci&oacute;n y despues a Impresoras. Seleccionamos el nombre de la impresora que estemos usando&nbsp; y dentro de Archivo escogemos Propiedades y&nbsp;marcamos &quot;Imprimir texto como gr&aacute;fico&quot; o &quot;Enviar True Type como bitmap&quot;',null);
