Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2703','0','MINISTERIO DE EDUCACI&Oacute;N, CULTURA Y DEPORTE: Estad&iacute;sticas sobre Educaci&oacute;n','&nbsp;    <a href="http://www.mecd.gob.es/servicios-al-ciudadano-mecd/estadisticas.html">MINISTERIO DE EDUCACI&Oacute;N, CULTURA Y DEPORTE.&nbsp; Estad&iacute;sticas sobre Educaci&oacute;n</a>. Incluye informaci&oacute;n sobre ense&ntilde;anzas no universitarias y universitarias, recursos econ&oacute;micos destinados a la educaci&oacute;n, relaci&oacute;n del sistema educativo con el exterior, formaci&oacute;n y mercado laboral e indicadores y publicaciones de s&iacute;ntesis.    &nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2702','0','INEE: Sistema estatal de indicadores','    <a href="http://www.mecd.gob.es/inee/sistema-indicadores.html">INEE: Sistema estatal de indicadores.</a> Estudios que se vienen realizando desde el a&ntilde;o 2000, donde se analizan datos sobre las siguientes dimensiones: contexto, recursos, escolarizaci&oacute;n, procesos&nbsp;y resultados educativos. Los datos aparecen desagregados seg&uacute;n sexo.&nbsp;',null);
