Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2685','0','INSTITUTO DE ESTUDIOS RIOJANOS','INSTITUTO DE ESTUDIOS RIOJANOS&nbsp;    <a target="_new" href="http://www.larioja.org/npRioja/default/defaultpage.jsp?idtab=447350">Becas de investigaci&oacute;n en temas de mujer:</a> el Instituto de Estudios Riojanos, organismo encuadrado org&aacute;nicamente en la Consejer&iacute;a de Educaci&oacute;n, Cultura, Juventud y Deportes de la Comunidad Aut&oacute;noma de La Rioja, convoca anulamente becas de investigaci&oacute;n en temas de mujer. El objetivo es fomentar, coordinar y difundir la cultura e investigaci&oacute;n sobre temas riojanos, entre otros, de inter&eacute;s para la mujer.',null);
