Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','159',null,'    Hezkuntza administrazioei, berdintasuneko erakundeei eta interesa duten erakunde zein pertsonei, emakumezkoen eta gizonezkoen hezkuntza esperientziak eta ezagutzak partekatzeko bidea ematea.    Hezkuntza komunitateari bi sexuak kontuan hartzen dituen, emakumezkoena onartu eta balioesten duena, gizonezkoen azpitik jarri gabe, hezitzeko modua ahalbidetzen duten baliabideak eskura jartzea.    Parte hartzeko, erraz sartu eta erabiltzeko, gunea sortzea.    Espainiar testuinguruan aplika daitekeen beste hainbat herrialdeko esperientzia berritzaileak eta originalak partekatzea.',null);
