Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','11','Texto','Nesta sec&ccedil;&atilde;o conhecereis a legisla&ccedil;&atilde;o vigente e a realidade estat&iacute;stica em mat&eacute;ria educativa e de igualdade de oportunidades atrav&eacute;s da Secretaria de Educa&ccedil;&atilde;o e o Organismo de Igualdade de cada Comunidade Aut&oacute;noma.Al&eacute;m disso, incluem-se os projectos e as iniciativas educativas que, a n&iacute;vel auton&oacute;mico, se desenvolvem &agrave; volta da coeduca&ccedil;&atilde;o.',null);
