Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3137','183','Texte','&Agrave; cette section, vous trouverez des mat&eacute;riels p&eacute;dagogiques &eacute;labor&eacute;s par le CIDE et l&rsquo;Institut de la Femme, parfois de fa&ccedil;on conjointe. Ce sont des mat&eacute;riels qui centrent votre regard sur la r&eacute;alit&eacute; &eacute;ducative que vivent les filles et les gar&ccedil;ons et qui proposent des formes d&rsquo;&eacute;duquer tenant compte de cette diff&eacute;rence sexuelle.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;',null);
