Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2626','17',null,'    S&#259; ofere at&acirc;t Administra&#355;iilor educative &#351;i Organismelor pentru egalitate, c&acirc;t &#351;i entit&#259;&#355;ilor &#351;i persoanelor interesate, un mediu propice schimbului de cuno&#351;tin&#355;e &#351;i de experien&#355;e educative la feminin &#351;i la masculin.     S&#259; ofere &icirc;ntregii comunit&#259;&#355;i educative accesul la resurse dovedind o modalitate de a educa care ia &icirc;n considerare ambele sexe, care recunoa&#351;te &#351;i pune &icirc;n valoare femininul, f&#259;r&#259; a-l subordina masculinului.     S&#259; creeze o pagin&#259; accesibil&#259;, u&#351;or de folosit &#351;i consultat, deschis&#259; particip&#259;rii.    S&#259; &icirc;mp&#259;rt&#259;&#351;easc&#259; experien&#355;e inovatoare originale cu alte &#355;&#259;ri, experien&#355;e care pot fi&nbsp;adaptate contextului spaniol.',null);
