Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3137','17',null,'&Icirc;n aceast&#259; sec&#355;iune pute&#355;i g&#259;si materiale educative elaborate, uneori &icirc;n comun, de CIDE &#351;i de Institutul femeii. Sunt materiale care se axeaz&#259; pe realitatea educativ&#259; a tinerelor &#351;i tinerilor &#351;i care propun forme de educa&#355;ie care iau &icirc;n calcul aceast&#259; diferen&#355;&#259; sexual&#259;. ',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3121','17','Ce este','&Icirc;n aceast&#259; sec&#355;iune pute&#355;i afla &#351;i aprofunda semnifica&#355;ia termenilor &#351;i expresiilor referitoare la educa&#355;ie, egaliatea &#351;anselor &#351;i mi&#351;carea femeilor.Nu ne propunem s&#259; definim fiecare termen &icirc;ntr-un mod univoc, dimpotriv&#259;, propunem diverse demersuri ale mai multor autoare &#351;i autori, &icirc;n scopul de a oferi chei de lectur&#259;.&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','17',null,'&Icirc;n aceast&#259; sec&#355;iune ve&#355;i cunoa&#351;te legisla&#355;ia &icirc;n vigoare &#351;i realitatea statistic&#259; &icirc;n ce prive&#351;te educa&#355;ia &#351;i egalitate a &#351;anselor prin intermediul&nbsp;Ministerului educa&#355;iei &#351;i al Organismului de promovare a egalit&#259;&#355;ii din fiecare Comunitate autonom&#259;.&Icirc;n plus, sunt incluse proiectele &#351;i ini&#355;iativele educative elaborate la nivelul comunit&#259;&#355;ilor autonome privitoare la coeduca&#355;ie.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3133','17',null,'Aceast&#259; sec&#355;iune con&#355;ine o colec&#355;ie a legilor &icirc;n vigoare privind coeduca&#355;ia &#351;i egalitatea &#351;anselor pe patru planuri: interna&#355;ional, comunitar, statal &#351;i autonom.&nbsp; Astfel, pute&#355;i g&#259;si documente &#351;i diverse texte de interes care se pot desc&#259;rca &icirc;n format pdf.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3128','17',null,'Aceast&#259; sec&#355;iune con&#355;ine at&acirc;t informa&#355;ii despre educa&#355;ie &#351;i femei &icirc;n lume, c&acirc;t &#351;i diverse ini&#355;iative coeducativo &icirc;ntreprinse de c&#259;tre femei &icirc;n lume. &Icirc;n plus, include o rubric&#259; dedicat&#259; vie&#355;ii &#351;i operei femeilor importante &icirc;n domeniul educativ &#351;i cultural, cu scopul de a construi o genealogie feminin&#259; care s&#259; ofere repere &#351;i s&#259; fac&#259; vizibil&#259; activitatea femeilor.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3129','17',null,'&Icirc;n aceast&#259; sec&#355;iune pute&#355;i g&#259;si grupuri de cercetare, asocia&#355;ii &#351;i organiza&#355;ii care se ocup&#259; cu chestiuni legate de situa&#355;ia femeii &#351;i&nbsp;de educa&#355;ie.',null);
