Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3387','11','Texto','Esta sec&ccedil;&atilde;o oferece uma selec&ccedil;&atilde;o de fontes estat&iacute;sticas em mat&eacute;ria coeducativa&nbsp;e de igualdade de oportunidades em quatro&nbsp;&acirc;mbitos: internacional, comunit&aacute;rio, estatal&nbsp;e auton&oacute;mico. Al&eacute;m disso, aparecem&nbsp;documentos de interesse que se podem descarregar em&nbsp;formato pdf.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3387','159',null,'Atal honetan, hezkuntza eta aukera berdintasunaren hainbat iturri estatistiko eskaintzen dira, lau arlotan: nazioartean, batasunean, estatuan eta erkidegoetan. Gainera, pdf formatuan jaits daitezkeen hainbat dokumentu interesgarri datoz.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3387','48',null,'Aquesta secci&oacute; ofereix una selecci&oacute; de fonts estad&iacute;stiques en mat&egrave;ria coeducativa i d&iquest;igualtat d&iquest;oportunitats en quatre &agrave;mbits: internacional, comunitari, estatal i auton&ograve;mic. A m&eacute;s, hi ha documents d&iquest;inter&egrave;s que poden descarregar-se en format pdf.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3122','48','Text','En aquesta secci&oacute; coneixereu la legislaci&oacute; vigent i la realitat estad&iacute;stica en mat&egrave;ria educativa i d&rsquo;igualtat d&rsquo;oportunitats a trav&eacute;s de la Conselleria d&rsquo;Educaci&oacute; i l&rsquo;Organisme d&rsquo;Igualtat de cada comunitat aut&ograve;noma.A m&eacute;s, s&rsquo;hi inclouen els projectes i les iniciatives educatives que a nivell auton&ograve;mic s&rsquo;estan duent a terme al voltant de la coeducaci&oacute;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3133','48','Text','En aquesta secci&oacute; trobareu recollida la legislaci&oacute; vigent en mat&egrave;ria coeducativa i d&rsquo;igualtat d&rsquo;oportunitats en quatre &agrave;mbits: internacional, comunitari, estatal i auton&ograve;mic. Tamb&eacute; hi trobareu documents i diversos textos d&rsquo;inter&egrave;s amb possibilitat de descarrega en format pdf.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3128','48','Text','Aquesta secci&oacute; recull informaci&oacute; sobre l&rsquo;educaci&oacute; i les dones al m&oacute;n, juntament amb les variades iniciatives coeducatives empreses per les dones al m&oacute;n. A m&eacute;s, inclou un apartat en qu&egrave; se cedeix un espai a la vida i l&rsquo;obra de dones significatives dins de l&rsquo;&agrave;mbit educatiu i cultural amb el prop&ograve;sit de construir una genealogia femenina que oferisca referents i visibilitze la labor de les dones.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3129','48','Text','En aquesta secci&oacute; podreu trobar grups d&rsquo;investigaci&oacute;, associacions i organitzacions que s&rsquo;interessen per les dones i l&rsquo;educaci&oacute;.',null);
