Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3569','0','Red de escuelas de Cultura y Paz','    <a href="http://www.educarex.es/web/guest/redes-escuelas-de-cultura-y-paz">Red de Escuelas de Cultura y Paz</a>: es la Red m&aacute;s joven, nacida en marzo de 2007. Est&aacute; formada por un conjunto de centros que quieren propiciar un conjunto de valores, actitudes, comportamientos y estilos de vida basados en el respeto a la vida, la no violencia, el respeto a los derechos humanos y el fomento de la igualdad entre hombres y mujeres. Pretenden incluir este esp&iacute;ritu como eje fundamental en sus Proyectos Educativos y Curricular de Centro.&nbsp;',null);
