Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3670','0','INSTITUTO ANDALUZ DE LA MUJER','INSTITUTO ANDALUZ DE LA MUJER    <a href="http://www.juntadeandalucia.es/institutodelamujer/index.php/estadisticas">Aproximaci&oacute;n a la realidad de mujeres y hombres en Andaluc&iacute;a</a>: incluye datos sobre poblaci&oacute;n, educaci&oacute;n, empleo, conciliaci&oacute;n y corresponsabilidad, salud, bienestar social, participaci&oacute;n e imagen y medios de comunicaci&oacute;n.    <a href="http://www.juntadeandalucia.es/institutodelamujer/index.php/estadisticas/enlaces-con-estadisticas-de-genero-del-instituto-de-estadistica-de-andalucia/mujeres-andaluzas-datos-basicos">Mujeres andaluzas. Datos b&aacute;sicos</a>:&nbsp;incluye informaci&oacute;n de 2010 y 2009 sobre mujeres y hombres, chicos y chicas en relaci&oacute;n con la educaci&oacute;n, poblaci&oacute;n y migraciones, familia y hogares, mercado de trabajo, salud y sanidad, protecci&oacute;n social, participaci&oacute;n, marginaci&oacute;n y violencia de g&eacute;nero.    <a href="http://www.juntadeandalucia.es/institutodelamujer/index.php/estadisticas/enlaces-con-estadisticas-de-genero-del-instituto-de-estadistica-de-andalucia/anuario-andaluz-mujeres-perspectivas-genero">Anuarios estad&iacute;sticos de Andaluc&iacute;a con perspectiva de g&eacute;nero</a>:&nbsp;incluye informaci&oacute;n desagregada por sexo sobre educaci&oacute;n, poblaci&oacute;n y migraciones, familia y hogares, mercado de trabajo, salud y sanidad, protecci&oacute;n social, participaci&oacute;n, marginaci&oacute;n y violencia dom&eacute;stica.    <a href="http://www.juntadeandalucia.es/institutodelamujer/index.php/estadisticas/estadisticas-sobre-violencia-contra-las-mujeres">Estad&iacute;sticas sobre violencia contra las mujeres    </a>',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3671','0','INSTITUTO ARAGON&Eacute;S DE LA MUJER','INSTITUTO ARAGON&Eacute;S DE LA MUJER    <a href="http://www.aragon.es/DepartamentosOrganismosPublicos/Organismos/InstitutoAragonesMujer/AreasTematicas/ci.OBSERVATORIO_VIOLENCIA_CONTRA_MUJER.detalleDepartamento?channelSelected=0#section4">Observatorio Aragon&eacute;s de violencia sobre la mujer</a>. Estudios, investigaciones e Informes.    <a href="http://www.aragon.es/DepartamentosOrganismosPublicos/Organismos/InstitutoAragonesMujer/AreasGenericas/ci.03_estadisticas.detalleDepartamento">Estad&iacute;sticas sobre las consultas realizadas en las asesor&iacute;as</a> social, jur&iacute;dica, laboral y psicol&oacute;gica, as&iacute; como en el tel&eacute;fono de consulta gratuito L&iacute;nea 900.<a href="http://www.aragon.es/DepartamentosOrganismosPublicos/Organismos/InstitutoAragonesMujer/AreasTematicas/ci.OBSERVATORIO_VIOLENCIA_CONTRA_MUJER.detalleDepartamento?channelSelected=0#section4">    </a><a href="http://www.aragon.es/DepartamentosOrganismosPublicos/Organismos/InstitutoAragonesMujer/AreasTematicas/ci.OBSERVATORIO_VIOLENCIA_CONTRA_MUJER.detalleDepartamento?channelSelected=0#section4">    </a>&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3672','0','INSTITUTO ARAGON&Eacute;S DE ESTAD&Iacute;STICA','INSTITUTO ARAGON&Eacute;S DE ESTAD&Iacute;STICA (IAEST)Normal021falsefalsefalseMicrosoftInternetExplorer4/* Style Definitions */table.MsoNormalTable{mso-style-name:"Tabla normal";mso-tstyle-rowband-size:0;mso-tstyle-colband-size:0;mso-style-noshow:yes;mso-style-parent:"";mso-padding-alt:0cm 5.4pt 0cm 5.4pt;mso-para-margin:0cm;mso-para-margin-bottom:.0001pt;mso-pagination:widow-orphan;font-size:10.0pt;font-family:"Times New Roman";mso-ansi-language:#0400;mso-fareast-language:#0400;mso-bidi-language:#0400;}    <a href="http://www.aragon.es/DepartamentosOrganismosPublicos/Organismos/InstitutoAragonesEstadistica/AreasTematicas/06_Analisis_Sociales_Justicia_Cultura_Y_Deporte/06_Estadisticas_Genero/01_DatosBasicosMujeres">Datos b&aacute;sicos sobre las mujeres en Arag&oacute;n (DBMA)</a>. Producto de difusi&oacute;n de informaci&oacute;n estad&iacute;stica p&uacute;blica de car&aacute;cter sint&eacute;tico, que recoge los indicadores m&aacute;s relevantes disponibles por sexo, posibilitando as&iacute; un mejor conocimiento de las diferencias en la situaci&oacute;n de las mujeres y hombres en la sociedad aragonesa.    <a href="http://www.aragon.es/DepartamentosOrganismosPublicos/Organismos/InstitutoAragonesEstadistica/AreasTematicas/03_Educacion_Y_Formacion?channelSelected=e1aca856c66de310VgnVCM2000002f551bacRCRD">Educaci&oacute;n y Formaci&oacute;n.</a> Incluye datos desagregados por sexo en relaci&oacute;n a la educaci&oacute;n universitaria, no universitaria, nivel educativo de la poblaci&oacute;n, transici&oacute;n educativa-laboral y Encuesta sobre la participaci&oacute;n de la poblaci&oacute;n adulta en las actividades de aprendizaje (EADA). Asimismo, ofrece informaci&oacute;n sobre gasto p&uacute;blico en educaci&oacute;n, becas y ayudas.&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3673','0','INSTITUTO ASTURIANO DE LA MUJER','&nbsp;INSTITUTO ASTURIANO DE LA MUJER<a href="http://www.asturestad.es/es/portal.do?IDM=33&amp;NM=2"></a>    <a href="http://institutoasturianodelamujer.com/iam/observatorio-de-igualdad-de-oportunidades/">Mujeres y Hombres en Asturias</a>: informes sobre la situaci&oacute;n de&nbsp;hombres y mujeres en Asturias desde 2002 hasta la actualidad: poblaci&oacute;n, educaci&oacute;n, trabajo (&aacute;mbito privado y mercado laboral, salud, participaci&oacute;n social y pol&iacute;tica.    <a href="http://institutoasturianodelamujer.com/iam/observatorio-de-igualdad-de-oportunidades/">Observatorio de Igualdad de Oportunidades</a>: su objetivo es la recogida sistem&aacute;tica de datos por medio de estad&iacute;sticas desagregadas por sexo, con el fin de conocer la realidad espec&iacute;fica de la igualdad de oportunidades entre mujeres y hombres en la Comunidad Aut&oacute;noma del Principado de Asturias.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3674','0',' ISTAC- INSTITUTO CANARIO DE ESTAD&Iacute;STICAS','INSTITUTO CANARIO DE ESTAD&Iacute;STICAS (ISTAC)    <a href="http://www.gobiernodecanarias.org/istac/temas_estadisticos/sociedad/educacion/">Estad&iacute;sticas sobre educaci&oacute;n</a> no universitaria, universitaria, no reglada y formaci&oacute;n profesional para el empleo, con datos desagregados por sexo.    <a href="http://www.gobiernodecanarias.org/istac/temas_estadisticos/sintesis/operacion_C00052A.html">Anuario estad&iacute;stico</a>: ofrece amplia informaci&oacute;n sobre distintos aspectos de la sociedad canaria a lo largo de distintos a&ntilde;os procedente de diversas fuentes (ISTAC, INE), con datos desagregados por sexo. Incluye un apartado dedicado a la educaci&oacute;n.    <a href="http://www.gobiernodecanarias.org/istac/temas_estadisticos/sociedad/culturadeporteocio/">Cultura, deportes y ocio</a>. Con informaci&oacute;n desagregada por sexo.&nbsp;&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3675','0','INSTITUTO CANARIO DE IGUALDAD','INSTITUTO CANARIO DE IGUALDAD    <a href="http://www.gobiernodecanarias.org/icigualdad/inicio/informacion_servicios/estadisticas_normativa/estadisticas.html">Estad&iacute;sticas</a>: incluye estudios diversos y enlaces con informaci&oacute;n general desagregada por sexo, as&iacute; como la relacionada con violencia contra las mujeres y con empleo.    <a href="http://www.gobiernodecanarias.org/istac/temas_estadisticos/sintesis/operacion_C00055A.html">Mujeres y hombres en Canarias</a>. An&aacute;lisis con datos, tablas y  gr&aacute;ficos que permiten visibilizar las diferentes situaciones y  condiciones de vida de mujeres y hombres en Canarias. Realizado a partir de la colaboraci&oacute;n con el  Instituto Canario de Estad&iacute;stica.    &nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3680','0','OBSERVATORIO EXTREME&Ntilde;O DE IGUALDAD Y EMPLEABILIDAD','OBSERVATORIO EXTREME&Ntilde;O DE IGUALDAD Y EMPLEABILIDAD    <a href="http://igualdadenred.gobex.es/index.php/noticias/425-el-observatorio-extremeno-de-igualdad-de-oportunidades-y-empleabilidad-publica-su-informe-personal-docente-y-otros-cargos-de-la-educacion-en-extremadura.html">Informe Personal docente y otros cargos de la Educaci&oacute;n en Extremadura</a>. Analiza datos procedentes de diversas operaciones estad&iacute;sticas para conocer las caracter&iacute;sticas del profesorado de los distintos niveles educativos en Extremadura, as&iacute; como las diferencias entre hombres y mujeres en las distintas ense&ntilde;anzas, en la toma de decisiones o en las retribuciones.&nbsp;',null);
