Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3890','0','Iniciativas, programas y documentos','&nbsp;    <a href="http://arabinfomall.bibalex.org/En/OrgData.aspx?orgid=330&amp;sectionid=5">Jordania: Programas para la educaci&oacute;n y la formaci&oacute;n profesional de las mujeres y las ni&ntilde;as. Women''''s Organization to Combat Illiteracy in Jordan (WOCI)</a>&nbsp;- Organizaci&oacute;n de Mujeres para Combatir el Analfabetismo&nbsp;    <a href="http://www.popcouncil.org/projects/40_IshraqSafeSpacesGirls.asp">Ishraq: Bringing Marginalized Rural Girls into Safe Learning Spaces in Upper Egypt -Llevar a Ni&ntilde;as y Adolescentes de &aacute;reas rurales marginadas a espacios de aprendizaje seguros en el Alto Egipto-</a>: Ishraq, que significa &quot;amanecer&quot; en &aacute;rabe, busca mejorar la educaci&oacute;n, salud y oportunidades sociales para las ni&ntilde;as vulnerables en las zonas rurales del Alto Egipto.&nbsp;&nbsp;    <a href="http://www.roomtoread.org/document.doc?id=508">Through Their Eyes, In Their Voices - a trav&eacute;s de sus ojos, en sus voces</a>: estudio que indaga en los factores sociales y educativos que contribuyen al &eacute;xito de las mujeres&nbsp; que van a la universidad. Con un enfoque en las estudiantes con desventajas socioecon&oacute;micas en &Aacute;frica y Asia, este estudio documenta las historias de 160 mujeres universitarias en cinco pa&iacute;ses.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3896','0','Enlaces','&nbsp;    <a href="http://www.wikigender.org/index.php/Gender_Equality_in_Iran">La igualdad en Ir&aacute;n (Wikigender)</a>:  art&iacute;culo con informaci&oacute;n sobre la situaci&oacute;n y desigualdades entre  hombres y mujeres en Ir&aacute;n, teniendo en cuenta leyes, costumbres y  libertades civiles.<a href="http://www.wikigender.org/index.php/Gender_Equality_in_Iraq">    </a>&nbsp;    <a href="http://www.wikigender.org/index.php/Gender_Equality_in_Iraq">La igualdad en Irak (Wikigender)</a>: informaci&oacute;n sobre la situaci&oacute;n y desigualdades entre mujeres y hombres en Irak.&nbsp;    <a href="http://www.wikigender.org/index.php/Gender_Equality_in_Jordan">Jordania: igualdad de g&eacute;nero (Wikigender)</a>: informaci&oacute;n sobre la situaci&oacute;n de las mujeres y las desigualades en Jordania.&nbsp;&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3888','0','Iniciativas, programas y documentos 1','&nbsp;&nbsp;    <a href="http://www.icrw.org/where-we-work/gender-equity-movement-schools-gems">GEMS, Gender Equity Movement in Schools - Movimiento por la Equidad de G&eacute;nero en las Escuelas- , ICRW:</a> Programa desarrollado en las Escuelas Municipales de Mumbai cuyo objetivo es promover la igualdad de g&eacute;nero en las relaciones entre ni&ntilde;as y ni&ntilde;os y superar el uso de la violencia. Desarrolla <a href="http://www.icrw.org/files/publications/GEMS%20Building%20Support%20for%20Gender%20Equality%20Adolescents_0.pdf ">informes</a> y emplea metodolog&iacute;as participativas tales como juegos de rol, juegos, debates y discusiones, que se llevan a cabo en las aulas durante el d&iacute;a escolar regular y se incluyen en una <a href="http://www.icrw.org/files/publications/Gender%20Equity%20Movement%20in%20Schools%20Campaign%20Guide.pdf ">Gu&iacute;a.</a>&nbsp;    <a href="http://www.theigc.org/article/cycling-school-increasing-high-school-enrollment-girls-bihar">IGC, International Growth Center: En bici a la escuela, Programa en Bihar</a> (India) destinado a facilitar la permanencia de las chicas en las escuelas m&aacute;s all&aacute; de la educaci&oacute;n primaria, brind&aacute;ndoles la posibilidad de que se trasladen en bicicleta.&nbsp;    <a href="http://www.ecf.org.in/">Equal Community Foundation</a>: programas orientados a favorecer la igualdad y las relaciones sanas&nbsp; a trav&eacute;s de la implicaci&oacute;n de los hombres y ni&ntilde;os, as&iacute; como el empoderamiento de las mujeres.<a href="http://www.ifuw-forums.org/blog/2011/07/18/girls-access-to-secondary-education/"></a>&nbsp;    <a href="http://www.popcouncil.org/projects/38_SchoolingLivelihoodsMarriage.asp">Population Council Bangladesh: La escuela, los medios de subsistencia, y el matrimonio</a>: proyecto que estudia el impacto de dos intervenciones a gran escala: un programa de becas de la escuela secundaria y un programa piloto para impartir habilidades para la vida de las adolescentes, ambos dise&ntilde;ados para mejorar las oportunidades de las ni&ntilde;as y el demorar la edad de matrimonio para favorecer su acceso a la educaci&oacute;n.&nbsp;    <a href="http://www.youtube.com/watch?v=ejHaCjT0kBU">OXFAM, Campa&ntilde;a para la educaci&oacute;n de las chicas en Pakist&aacute;n</a>,&nbsp; Dunya Aur Larki.&nbsp;    <a href="http://www.iiep.unesco.org/fileadmin/user_upload/Cap_Dev_Training/Virtual_Institute/pdf/Forums/2012/Chudgar_Amita_Sankar_Vyjayanthi_2008_teacher_gender_student_achievement.pdf">UNESCO: La relaci&oacute;n entre el g&eacute;nero del profesorado y el rendimiento del alumnado: estudio de cinco estados de la India</a>, Amita Chudgara y Sankarb Vyjayanthi, IIEP.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3897','0','Iniciativas, programas y documentos','&nbsp;    <a href="http://www.mtnforum.org/en/content/women-mind-educational-needs-women-central-asia-general-recommendations-and-strategies-devel">Las necesidades educativas de las mujeres en Asia Central. Recomendaciones y Estrategias para el Desarrollo</a> (Women in Mind: Educational Needs of Women in Central Asia-General Recommendations and Strategies for Development). Nuria R. Ismagilova, Khujand, Tayikist&aacute;n: art&iacute;culo sobre los obst&aacute;culos que dificultan el acceso y permanencia de las mujeres en la escuela y la universidad. Incluye tambi&eacute;n recomendaciones y estrategias para superar esta situaci&oacute;n.&nbsp;    <a href="http://www.genderstudies.info/english/eng_jour2.php">Estereotipos de g&eacute;nero en los libros de textro de primaria en Uzbekist&aacute;n</a>. Dinara Alimdjanova, Center for Gender Studies, Tashkent&nbsp;    <a href="http://www.genderstudies.info/english/eng_text.php">Educaci&oacute;n no-formal en relaci&oacute;n al g&eacute;nero y la paz para la juventud de Kyrguist&aacute;n: an&aacute;isis de necesidades, teor&iacute;as y metodolog&iacute;as</a>, Anna Kirey.&nbsp;    <a href="http://www.eurasianet.org/departments/insight/articles/pp110903.shtml">Las chicas afrontan la discriminaci&oacute;n en la escuela. Eurasia.Net</a>: art&iacute;culo que relata las dificultades y la discriminaci&oacute;n que afrontan las chicas en su acceso a la educaci&oacute;n reglada.<a href="http://rrojasdatabank.info/agfrank/gender_relations.html">    </a>&nbsp;    <a href="http://rrojasdatabank.info/agfrank/gender_relations.html">Sobre las relaciones de g&eacute;nero y la historia en Asia Central</a>. Extra&iacute;do de Adre Gunder Frank (1992): Free University Press, Amsterdam.&nbsp;    <a href="http://www.unifem.org/worldwide/europe_cis/">ONU-MUJERES (UN-WOMEN)</a>: informaci&oacute;n sobre la situaci&oacute;n de las mujeres y proyectos de ONU-Mujeres en Asia Central.&nbsp;    <a href="http://awid.org/esl/Library/El-impacto-de-la-crisis-global-en-las-mujeres-de-Asia-Central">El impacto de la crisis global en las mujeres de Asia Central</a>. AWID, Asociaci&oacute;n para los Derechos de la Mujer y el Desarrollo.&nbsp;    <a href="http://www.omct.org/files/2004/07/2409/esp_2002_08_uzbekistan.pdf">OMCT. Uzbekist&aacute;n: Informe presentado al Comit&eacute; contra la Tortura. Resumen del informe &ldquo;Violencia contra las mujeres en Uzbekist&aacute;n&rdquo;</a>.&nbsp;&nbsp;&nbsp;&nbsp;',null);
