Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4175','0','Premios Francisca de Aculodi','    <a href="http://www.berdintasuna.ehu.es/p234-content/es/contenidos/premio_concurso/conv_premio_francisca_aculodi/es_premio/francisca_de_aculodi.html">Premios Francisca de Aculodi a la inclusi&oacute;n de la perspectiva de g&eacute;nero en los trabajos de fin de grado</a>.&nbsp;&nbsp; Universidad del Pa&iacute;s Vasco. La UPV/EHU convoca este premio con el objetivo de impulsar y dar a conocer la inclusi&oacute;n de la perspectiva de g&eacute;nero en los trabajos de fin de grado. El nombre del Premio es Francisca de Aculodi, siguiendo el camino iniciado en los anteriores de recuperar los nombres y las aportaciones de aquellas mujeres, vascas de origen o por adopci&oacute;n, que fueron pioneras en sus diferentes profesiones.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4176','0','DIRECCI&Oacute;N DE ATENCI&Oacute;N A LAS V&Iacute;CTIMAS DE VIOLENCIA DE G&Eacute;NERO','DIRECCI&Oacute;N DE ATENCI&Oacute;N A LAS V&Iacute;CTIMAS DE VIOLENCIA DE G&Eacute;NERO    <a href="http://www.interior.ejgv.euskadi.net/r42-4100/es/contenidos/informacion/ez_isildu/es_06/quien_bien_te_quiere_te_hara_feliz.html">Ez isildu!</a>&nbsp; Programa que, adem&aacute;s de ofrecer orientaciones en relaci&oacute;n a la violencia sexista y qu&eacute; se puede hacer si una persona la est&aacute; padeciendo, in.cluye un programa de prevenci&oacute;n, con secciones como Quien bien te quiere te har&aacute; feliz.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4177','0','Departamento de Educaci&oacute;n, Pol&iacute;tica Lingü&iacute;stica y Cultura','DEPARTAMENTO DE EDUCACI&Oacute;N, POL&Iacute;TICA LING&Uuml;&Iacute;STICA Y CULTURA    <a href="http://www.hezkuntza.ejgv.euskadi.net/r43-573/es/contenidos/informacion/dig_publicaciones_innovacion/es_conviven/adjuntos/coeducacion/920006c_Pub_EJ_hezkidetza_plana_c.pdf">Plan Director para la coeducaci&oacute;n y la prevenci&oacute;n de la violencia de g&eacute;nero en el sistema educativo:</a><a href="http://www.hezkuntza.ejgv.euskadi.net/r43-573/es/contenidos/informacion/dig_publicaciones_innovacion/es_conviven/adjuntos/coeducacion/920006c_Pub_EJ_hezkidetza_plana_c.pdf"></a> Orientado a impulsar un modelo basado en el desarrollo integral de las personas al margen de los estereotipos y roles en funci&oacute;n del sexo, que eduque en el rechazo a toda forma de discriminaci&oacute;n y de violencia sexista, y que posibilite una orientaci&oacute;n acad&eacute;mica y profesional sin sesgo de g&eacute;nero, contando con la participaci&oacute;n y la implicaci&oacute;n de todas las personas de la comunidad educativa.&nbsp; Incluye acciones de sensibilizaci&oacute;n y formaci&oacute;n para el profesorado y el alumnado; acciones orientadas a la transformaci&oacute;n de la escuela mixta en escuela coeducativa; nombramiento de responsables de coeducaci&oacute;n en los centros escolares y en&nbsp; cada uno de los servicios del Departamento, as&iacute; como representantes de igualdad en los &oacute;rganos de m&aacute;xima representaci&oacute;n.&nbsp;&nbsp;',null);
