Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4273','0','Detecta Amor','    LA JUNTA DE ANDALUC&Iacute;A PONE EN MARCHA UNA <a href="http://www.juntadeandalucia.es/iam/index.php/noticias/1706-la-junta-pone-en-marcha-una-aplicacion-movil-para-promover-en-la-juventud-andaluza-modelos-de-pareja-sanos-e-igualitarios">APLICACI&Oacute;N M&Oacute;VIL PARA PROMOVER EN LA JUVENTUD ANDALUZA MODELOS DE PAREJA SANOS E IGUALITARIOS</a>Detecta Amor es una aplicaci&oacute;n para m&oacute;viles (APP) orientada a ofrecer a la juventud, de una forma l&uacute;dica, din&aacute;mica y pedag&oacute;gica, las claves para desarrollar relaciones afectivas sanas e igualitarias. La aplicaci&oacute;n, que tiene como p&uacute;blico objetivo a m&aacute;s de 434.000 personas de entre 15 y 19 a&ntilde;os, se enmarca dentro de la campa&ntilde;a S&iacute; es Amor,&nbsp;puesta en marcha este oto&ntilde;o para promover entre la juventud andaluza modelos de pareja respetuosos y basados en la igualdad, as&iacute; como para concienciar a este sector de la poblaci&oacute;n sobre la relaci&oacute;n existente entre roles sexistas y violencia de g&eacute;nero. &nbsp;&nbsp;<a href="http://www.juntadeandalucia.es/iam/index.php/noticias/1706-la-junta-pone-en-marcha-una-aplicacion-movil-para-promover-en-la-juventud-andaluza-modelos-de-pareja-sanos-e-igualitarios">M&aacute;s informaci&oacute;n</a>&nbsp; - &nbsp;<a href="http://www.juntadeandalucia.es/iam/index.php/coeducacion/app-detectamor">Descarga de la aplicaci&oacute;n</a>&nbsp;&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4274','0','250 MILLONES DE NI&Ntilde;AS Y NI&Ntilde;OS NO SABEN LEER Y ESCRIBIR','    <a href="http://www.intered.org/node/710">250 MILLONES DE NI&Ntilde;AS Y NI&Ntilde;OS NO SABEN LEER Y ESCRIBIR</a>, INCLUSO DESPU&Eacute;S DE HABER ASISTIDO CUATRO A&Ntilde;OS A LA ESCUELA&nbsp;&nbsp;El pasado 5 de octubre fue el D&iacute;a del Profesorado. Seg&uacute;n datos de la UNESCO en su &uacute;ltimo informe de <a href="http://www.unesco.org/new/es/education/themes/leading-the-international-agenda/efareport/the-report-and-efa/">Educaci&oacute;n para Todos</a>, en el mundo hay 250 millones de ni&ntilde;as y ni&ntilde;os que no saben leer, escribir, ni contar, incluso despu&eacute;s de haber asistido cuatro a&ntilde;os al colegio; lo que significa que no han recibido una educaci&oacute;n adecuada. Como ha se&ntilde;alado la directora general de la UNESCO, Irina Bokova, `La educaci&oacute;n debe venir en primer lugar, pero no cualquier tipo de educaci&oacute;n, sino una educaci&oacute;n de calidad&acute;.El profesorado es fundamental en todo el proceso de la ense&ntilde;anza y tiene la llave para mejorar el futuro de su alumnado. El poder transformador de la educaci&oacute;n de calidad permite el desarrollo personal, la equidad de g&eacute;nero y la erradicaci&oacute;n de la pobreza.Fuente:<a href="http://www.intered.org/"> Intered</a>. <a href="http://www.intered.org/node/710">M&aacute;s informaci&oacute;n</a>&nbsp;-&nbsp;<a href="http://www.youtube.com/watch?v=B4mhGsfEeDg&amp;feature=youtu.be">Documental La educaci&oacute;n es poder</a>&nbsp;&nbsp;',null);
