Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4008','0','Educaci&oacute;n en Igualdad','    <a href="http://igualdade.xunta.es/es/content/educacion-en-igualdad">Educaci&oacute;n en igualdad</a>: &aacute;rea de actuaci&oacute;n orientada a la coeducaci&oacute;n en las aulas, prestando atenci&oacute;n al estilo educativo, al proceso de transformaci&oacute;n social que apunta a una convivencia entre los sexos alejada de la opresi&oacute;n y tendente a una relaci&oacute;n de justicia y respeto y en inter&eacute;s de una formaci&oacute;n integral y una efectiva igualdad de oportunidades entre hombres y mujeres. Incluye materiales did&aacute;cticos y estudios.',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('4009','0','Escuela de Profesionales de la Igualdad','    <a href="http://igualdade.xunta.es/es/programas/escuela-para-profesionales-de-la-igualdad-2013">Escuela de Profesionales de la Igualdad</a>: acciones formativas on-line en relaci&oacute;n a la igualdad y la prevenci&oacute;n e intervenci&oacute;n ante la violencia sexista dirigidas al personal t&eacute;cnico dependiente de las administraciones locales gallegas.',null);
