Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('2662','0','Instituto Estad&iacute;stico de Extremadura','INSTITUTO ESTAD&Iacute;STICO DE EXTREMADURA    <a href="http://estadistica.gobex.es/listadon2.php?id=10">Informaci&oacute;n estad&iacute;stica sobre Educaci&oacute;n, Formaci&oacute;n e Investigaci&oacute;n</a>    El <a href="http://estadistica.gobex.es/contenido.php?seccion=2&amp;pag=3">Anuario estad&iacute;stico</a> y&nbsp; <a href="http://estadistica.gobex.es/contenido.php?pag=12">Extremadura en cifras</a> incluyen algunos datos desagregados por sexo y un apartado dedicado a la educaci&oacute;n. &nbsp;&nbsp;&nbsp;',null);
