Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3791','0','Curso','&nbsp;    <a href="http://formacionprofesorado.educacion.es/index.php/es/experiencias/124-coeducacion-dos-sexos-en-un-solo-mundo">Coeducaci&oacute;n: dos sexos en un solo mundo</a> surge a partir de la colaboraci&oacute;n entre el INTEF (Instituto Nacional de Tecnolog&iacute;as Educativas y Formaci&oacute;n del Profesorado) y el Instituto de la Mujer. Se orienta al profesorado, facilitando el reconocimiento de su propia pr&aacute;ctica educativa, con el fin de mejorarla y avanzar en la creaci&oacute;n de un ambiente coeducativo en el que la convivencia entre personas de ambos sexos suponga intercambio y enriquecimiento, y no un caldo de cultivo para la desigualdad, la violencia, los prejuicios o los estereotipos.     ',null);
