Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3562','0','CPR','    <a href="http://www.cprceuta.es/Revista/Numero2/Agenda/igualdad.html">Jornadas &quot;Hacia la igu</a><a href="http://www.cprceuta.es/Revista/Numero2/Agenda/igualdad.html">aldad real entre hombres y mujeres&quot;</a>: desarrolladas con el objetivo de continuar avanzando en el camino hacia la igualdad entre mujeres y hombres y la educaci&oacute;n no sexista  en todos sus &aacute;mbitos. Incluyen&nbsp; la divulgaci&oacute;n de los estudios e investigaciones realizados por  los y las participantes en el Curso Especializaci&oacute;n en Educaci&oacute;n no sexista  y Formaci&oacute;n en g&eacute;nero. Estos estudios indagan en el imaginario amoroso en la adolescencia, el sexismo en los cuentos y en las pel&iacute;culas de Disney, modelos de g&eacute;nero, el ejercicio de la docencia y la educaci&oacute;n afectivo-sexual.    <a href="http://www.cprceuta.es/Asesorias/coeducacion/coeducacion.html">Espacio del CPR dedicado a la Coeducaci&oacute;n</a>. Incluye enlaces de inter&eacute;s, as&iacute; como informaci&oacute;n sobre Jornadas y eventos educativos.&nbsp;&nbsp;&nbsp;&nbsp;',null);
