Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3573','0','programas','    <a href="http://www.educarex.es/web/guest/proeduayu/educacion-para-la-igualdad-y-la-no-violencia">Educar en relaci&oacute;n para prevenir la violencia:</a> curso de formaci&oacute;n destinado al personal de la Junta de  Extremadura dentro del Plan de Formaci&oacute;n de la Escuela de Administraci&oacute;n  P&uacute;blica (E.A.P.). Las personas asistentes han formado parte de los equipos de educaci&oacute;n social de Centros de  Menores, Orientaci&oacute;n y personal del Instituto de la Mujer de  Extremadura.    <a href="http://www.educarex.es/web/guest/proeduayu/educacion-para-la-igualdad-y-la-no-violencia">Programas para la Igualdad y la No violencia</a>: incluye programas como Mujer, Arte y G&eacute;nero, que comprende una conferencia, exposici&oacute;n y proyecci&oacute;n de videos de 6 artistas con perspectiva de g&eacute;nero. Performance con la participaci&oacute;n del alumnado de secundaria.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3575','0','Premios Tom&aacute;s Garc&iacute;a Verdejo','    <a href="http://www.educarex.es/web/guest/premios-tomas-garcia-verdejo">Premios Tom&aacute;s Garc&iacute;a Verdejo</a> a las buenas pr&aacute;cticas educativas en centros educativos p&uacute;blicos de  niveles previos a la Universidad: la convocatoria establece como tema de entidad prioritaria, entre otros, el trabajo en valores y actitudes socialmente compartidos, tales como el fomento de la igualdad entre mujeres y hombres y la educaci&oacute;n social y emocional.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3576','0','Premios Educaci&oacute;n en Valores','    <a href="http://www.educarex.es/web/guest/premios-educacion-en-valores">Premios Educaci&oacute;n en Valores:</a> el objeto de estos premios es reconocer y premiar al alumnado, y por extensi&oacute;n a sus centros educativos, por la realizaci&oacute;n, durante el curso escolar, de experiencias encaminadas al desarrollo de la educaci&oacute;n en valores. Se conceder&aacute;n a los estudios, trabajos de investigaci&oacute;n y actividades que versen sobre aspectos como la educaci&oacute;n para la igualdad entre hombres y mujeres; educaci&oacute;n para la paz, la no violencia y la solidaridad; educaci&oacute;n sexual y para la salud; educaci&oacute;n ambiental; educaci&oacute;n en el respeto a la diversidad desde la compensaci&oacute;n de desigualdades y educaci&oacute;n en la multiculturalidad, como eliminaci&oacute;n de toda forma de racismo, discriminaci&oacute;n, xenofobia e intolerancia.    &nbsp;&nbsp;',null);
