Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3699','0','EUSTAT','EUSTAT    <a href="http://www.eustat.es/estadisticas/tema_279/opt_0/tipo_10/ti_Educacion/temas.html#axzz1RmOq2z5d">Estad&iacute;sticas sobre Educaci&oacute;n sobre mujeres y hombres</a> en diferentes niveles, modalidades y ramas de la educaci&oacute;n reglada.&nbsp;    <a href="http://www.eustat.es/estadisticas/idioma_c/tipo_10/arbol.html#axzz1RmOq2z5d">&ldquo;Igualdad de mujeres y hombres&rdquo;:</a> permite conocer las diferencias entre hombres y mujeres en relaci&oacute;n a diversos &iacute;tems relacionados con el &aacute;mbito demogr&aacute;fico, laboral, sanitario, educativo, cultural, deportivo, la inclusi&oacute;n, los h&aacute;bitos sociales, la justicia y la violencia contra las mujeres, las elecciones y el medio ambiente.&nbsp;    <a href="http://www.eustat.es/estadisticas/opt_0/id_299/ti_Educacion/subarbol.html#axzz1RmOq2z5d">Estad&iacute;sticas sobre Educaci&oacute;n:</a> incluye datos desagregados por sexo sobre el alumnado, el profesorado y los centros p&uacute;blicos y privados del Pa&iacute;s Vasco, as&iacute; como informaci&oacute;n relacionada con el gasto y financiaci&oacute;n de la educaci&oacute;n reglada.&nbsp;    <a href="http://www.eustat.es/estadisticas/opt_0/tipo_2/id_299/ti_Educacion/arbol.html#axzz1RmOq2z5d">Indicadores gr&aacute;ficos sobre educaci&oacute;n, investigaci&oacute;n y otros &aacute;mbitos</a>: ofrece gr&aacute;ficos con informaci&oacute;n desagregada por sexo sobre alumnado, profesorado, centros educativos, nivel de instrucci&oacute;n e investigaci&oacute;n, as&iacute; como en relaci&oacute;n a otras cuestiones de los &aacute;mbitos demogr&aacute;fico, econ&oacute;mico, social, cultural, sanitario y tecnol&oacute;gico.&nbsp;    <a href="http://www.eustat.es/bancopx/spanish/Sociedad/Educaci%C3%B3n/Ense%C3%B1anza/Indicadores%20de%20la%20ense%C3%B1anza/Indicadores%20de%20la%20ense%C3%B1anza.html#axzz1RmOq2z5d">Indicadores de la ense&ntilde;anza</a>: informaci&oacute;n, en muchos casos desagregada por sexo, sobre la ense&ntilde;anza reglada y la actividad escolar (tasas de escolaridad, ratios profesorado/alumnado, tasas de idoneidad, porcentajes de alumnado que aprueba, que repite y que promociona&hellip;).    <a href="http://www.eustat.es/estad/temalista.asp?tema=279&amp;tipo=3&amp;opt=0&amp;mas=&amp;idioma=c&amp;otro=#axzz1yn4AEgib">Informe Mujeres y hombres en Euskadi</a>: informe de la situaci&oacute;n de hombres y mujeres en cuanto a la educaci&oacute;n, la demograf&iacute;a, la salud, cultura y deportes, inclusi&oacute;n social, h&aacute;bitos sociales, elecciones y medio ambiente.&nbsp;&nbsp;',null);
