Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3882','0','UNESCO: Publicaciones sobre g&eacute;nero y educaci&oacute;n','    <a href="http://www.uis.unesco.org/Education/Pages/gender-education.aspx">UNESCO: Publicaciones sobre g&eacute;nero y educaci&oacute;n.</a> Incluye el <a href="http://www.unesco.org/new/en/education/themes/leading-the-international-agenda/gender-and-education/resources/the-world-atlas-of-gender-equality-in-education/">Atlas sobre la Igualdad de G&eacute;nero en la Educaci&oacute;n</a>, informe sobre la situaci&oacute;n educativa de mujeres y hombres a lo largo de todo el mundo, con datos, cifras, gr&aacute;ficos, mapas y an&aacute;lisis en relaci&oacute;n al acceso y permanencia en los diferentes niveles educativos. Asimismo, ofrece <a href="http://www.uis.unesco.org/Education/Pages/unesco-e-atlas-launch.aspx">publicaciones sobre la investigaci&oacute;n y el desarrollo experimental, las ni&ntilde;as y ni&ntilde;os sin escolarizar y el profesorado en el mundo</a> (algunas de ellas en espa&ntilde;ol).',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3884','0','Hellen Keller y Anne Sullivan','Hellen Keller (1880-1968), primera mujer sordociega que fue a la universidad, activista, autora y oradora:    <a href="http://es.wikipedia.org/wiki/Helen_Keller">Vida, obra y enlaces</a> (Wikipedia)    <a href="http://www.apocatastasis.com/helen-keller.php#axzz20mAvbRhp">Biograf&iacute;a, aportaciones y reportaje sobre su vida</a>    <a href="http://issuu.com/atalantaweb/docs/66_-_keller_issuu?mode=embed&amp;layout=http%3A%2F%2Fskin.issuu.com%2Fv%2Fcolor%2Flayout.xml&amp;backgroundColor=000000&amp;showFlipBtn=true&amp;autoFlip=true&amp;autoFlipTime=6000">Libro &quot;El mundo en el que vivo&quot; (1908)</a>. Imaginatio Vera. Vila&uuml;r: Ediciones Atalanta.    <a href="http://www.swedenborg.es/helen_keller/helen_keller.htm">Libro &quot;Luz en mi oscuridad&quot;</a>.&nbsp;Anne Sullivan (1866-1936), maestra de personas sordociegas y escritora    <a href="http://es.wikipedia.org/wiki/Anne_Sullivan">Vida, referencias, enlaces</a> (Wikipedia).    <a href="http://www.afb.org/asm/">Biograf&iacute;a (en ingl&eacute;s, Fundaci&oacute;n Americana para personas ciegas)</a>.    <a href="http://mujeresquehacenlahistoria.blogspot.com.es/2009/04/siglo-xix-annie-sullivan.html">Biograf&iacute;a</a>, en Mujeres que hacen la Historia    <a href="http://www.imdb.com/title/tt0056241/awards">Pel&iacute;cula &quot;El milagro de Anna Sullivan (The miracle worker, 1962)</a>&quot;&nbsp;',null);
