Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3890','0','Iniciativas, programas y documentos','&nbsp;    <a href="http://arabinfomall.bibalex.org/En/OrgData.aspx?orgid=330&amp;sectionid=5">Jordania: Programas para la educaci&oacute;n y la formaci&oacute;n profesional de las mujeres y las ni&ntilde;as. Women''''s Organization to Combat Illiteracy in Jordan (WOCI)</a>&nbsp;- Organizaci&oacute;n de Mujeres para Combatir el Analfabetismo&nbsp;    <a href="http://www.popcouncil.org/projects/40_IshraqSafeSpacesGirls.asp">Ishraq: Bringing Marginalized Rural Girls into Safe Learning Spaces in Upper Egypt -Llevar a Ni&ntilde;as y Adolescentes de &aacute;reas rurales marginadas a espacios de aprendizaje seguros en el Alto Egipto-</a>: Ishraq, que significa &quot;amanecer&quot; en &aacute;rabe, busca mejorar la educaci&oacute;n, salud y oportunidades sociales para las ni&ntilde;as vulnerables en las zonas rurales del Alto Egipto.&nbsp;&nbsp;    <a href="http://www.roomtoread.org/document.doc?id=508">Through Their Eyes, In Their Voices - a trav&eacute;s de sus ojos, en sus voces</a>: estudio que indaga en los factores sociales y educativos que contribuyen al &eacute;xito de las mujeres&nbsp; que van a la universidad. Con un enfoque en las estudiantes con desventajas socioecon&oacute;micas en &Aacute;frica y Asia, este estudio documenta las historias de 160 mujeres universitarias en cinco pa&iacute;ses.&nbsp;',null);
