Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3944','0','Iniciativas y programas 1','&nbsp;    <a href="http://www.napequity.org/education-foundation/mission/">NAPE    , The National Alliance for Partnerships in Equity (Alianza Nacional   de  Asociaciones para la Igualdad) &ndash; Education Foundation</a>:    orientada a la realizaci&oacute;n de investigaciones en materia de  educaci&oacute;n,   fuerza de trabajo y pol&iacute;ticas de desarrollo; la recogida,  elaboraci&oacute;n y   difusi&oacute;n de las mejores pr&aacute;cticas y recursos; el trabajo  junto con  otras  organizaciones para superar los prejuicios y los  estereotipos en  la  educaci&oacute;n y el empleo; el desarrollo de materiales y  programas para   promover carreras no tradicionales, la mejora de la  capacidad de las   escuelas y los cambios en las comunidades, escuelas y  negocios.&nbsp;    <a href="http://www.genderequitycollaborative.org/index.php">Collaborative for Gender Equity</a>:    proyecto orientado a alentar las estudiantes de secundaria para  seguir   una carrera en las tecnolog&iacute;as emergentes. Entre sus  actividades,&nbsp; se   incluyen el desarrollo de un programa de mentor&iacute;a en  l&iacute;nea, una serie  de  talleres y un&nbsp; curso online para fomentar el  desarrollo profesional  de  Equidad de G&eacute;nero en la Ciencia, Tecnolog&iacute;a,  Ingenier&iacute;a y  Matem&aacute;ticas  para profesorado.&nbsp;    <a href="http://www.aauw.org">AAUW, American Association for University Women: Atravesando las barreras de las mujeres y las ni&ntilde;as</a>:   amplia red con sede en diferentes estados de EEUU, as&iacute; como a lo largo   del mundo, dedicada a promover la igualdad y la mejora de la situaci&oacute;n   de las mujeres a trav&eacute;s de la educaci&oacute;n, la investigaci&oacute;n y la defensa   de los derechos humanos. Incluye numerosos <a href="http://www.aauw.org/learn/LeadershipPrograms/index.cfm">programas y proyectos</a> (como Leadership Program), as&iacute; como <a href="http://www.aauw.org/learn/publications/">publicaciones.</a><a href="http://www.aauw.org/learn/publications/">    </a>    &nbsp;    <a href="http://women.cs.cmu.edu/ada/">The ADA Project (TAP), Proyecto ADA</a>:   proyecto que toma el nombre de ADA Lovelace, la creadora del lenguaje   inform&aacute;tico que se denomina as&iacute;. Su web incluye informaci&oacute;n sobre   conferencias, proyectos, grupos y organizaciones. debates, becas y   subvenciones, mujeres notables en Ciencias de la Inform&aacute;tica y las   Tecnolog&iacute;as, y otros sitios accesibles electr&oacute;nicamente.&nbsp;    <a href="http://ms.foundation.org/">MS. Foundation</a>:   proyectos orientados al cambio social y cultural para el logro de la   equidad y la mejora de la situaci&oacute;n de las mujeres de diferentes clases y   etnias, con especial atenci&oacute;n a la justicia econ&oacute;mica, la salud, la   democracia y la prevenci&oacute;n y el fin de la violencia.&nbsp;    <a href="http://www.girlsinc.org/about/programs/girls-inc-programs.html">GIRLS Inc: programas y experiencias</a>   que ayudan a las chicas a atravesar las barreras del g&eacute;nero,  econ&oacute;micas  y sociales y a alcanzar logros acad&eacute;micos, llevar una vida  sana y  activa f&iacute;sicamente, administrar el dinero, ir m&aacute;s all&aacute; de los  mensajes  medi&aacute;ticos y descubrir su inter&eacute;s por la ciencia, la  tecnolog&iacute;a, la  ingenier&iacute;a y matem&aacute;ticas.&nbsp;',null);
Insert into BLOQUETEXTO_I (ID,ID_IDIOMA,TITULO,TEXTO,ALTIMAGEN) values ('3945','0','Iniciativas y programas 2','    <a href="http://feminist.org/education/FMFprogram.asp">Programa Igualdad en la Educaci&oacute;n, Feminist Education Foundation</a>:    orientado a promover la coeducaci&oacute;n en las aulas. En la web, incluye    una gu&iacute;a para favorecer la educaci&oacute;n y aborda tambi&eacute;n cuestiones como  la   salud femenina, la prevenci&oacute;n de la violencia sexista, las mujeres  en   el deporte y los derechos sexuales y reproductivos.&nbsp;    <a href="http://www.iwitts.org/projects">IWITTS,   National Institute for women in Trades, Technology &amp; Science   (Instituto Nacional para las Mujeres en los Oficios, la Tecnolog&iacute;a y la   Ciencia</a>): Programas, proyectos, talleres y materiales&nbsp;   educativos orientados al logro de la igualdad entre mujeres y hombres en   las aulas, el mundo laboral, la ciencia y las tecnolog&iacute;as.&nbsp;    <a href="http://www.nsf.gov/discoveries/disc_summ.jsp?cntn_id=123752&amp;org=NSF">CalWomenTech Scale Up Project: Proven Tools Attract Women to STEM Training Programs. Herramientas para atraer a las mujeres a Programas de Formaci&oacute;n relacionada con las Tecnolog&iacute;as</a>. National Science Foundation.&nbsp;    <a href="https://ieeetv.ieee.org/player/html/viewer?dl=#nerd-girls">Nerd Girls: documental sobre mujeres ingenieras en proyectos innovadores</a>.&nbsp;&nbsp;',null);
