Insert into CONTENIDO_AGENDA_I (ID_CONTENIDO,ID_IDIOMA,TITULO,CONTENIDO,SUBTITULO,LUGAR) values ('222','0','I Congreso Andaluz de Empleo e Igualdad','El grupo de investigaci&oacute;n MIDO (M&eacute;todos de Investigaci&oacute;n y Diagn&oacute;stico en Orientaci&oacute;n), de la Facultad de Ciencias de la Educaci&oacute;n de la Universidad de Sevilla, junto a AFOE (Asociaci&oacute;n para la formaci&oacute;n, el ocio y el empleo), organizan este Congreso como punto de difusi&oacute;n, informaci&oacute;n y debate a trav&eacute;s de mesas redondas, conferencias, talleres y comunicaciones, sobre la igualdad efectiva de hombres y mujeres en el &aacute;mbito laboral.',null,'Sevilla');
